import "./App.css";
import UploadForm from "./Components/UploadForm";
import Photo from "./Components/Photo";
import { useEffect, useState } from "react";

function App() {
  const [photos, setPhotos] = useState([]);
  const [editingPhoto, setEditingPhoto] = useState(null);
  const [page, setPage] = useState(1);

  const fetchPhotos = async () => {
    try {
      const response = await fetch(`http://localhost:3001/photos?page=${page}`);
      const data = await response.json();
      setPhotos(data);
    } catch (err) {
      console.error("Error fetching photos", err);
    }
  };

  useEffect(() => {
    fetchPhotos();
  }, [page]);

  const handleEdit = (photo) => {
    setEditingPhoto(photo);
  };

  const handleUpdate = async (updatedPhoto) => {
    try {
      const formData = new FormData();
      formData.append('title', updatedPhoto.title);
      formData.append('description', updatedPhoto.description);
      if (updatedPhoto.newPhoto) {
        formData.append('photo', updatedPhoto.newPhoto);
      }
      await fetch(`http://localhost:3001/photos/${updatedPhoto._id}`, {
        method: "PUT",
        body: formData,
      });
      fetchPhotos();
    } catch (err) {
      console.error("Error updating photo", err);
    }
  };


  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:3001/photos/${id}`, { method: "DELETE" });
      fetchPhotos();
    } catch (err) {
      console.error("Error deleting photo", err);
    }
  };

  return (
    <div>
      <UploadForm />
      {editingPhoto ? (
        <Photo
          key={editingPhoto._id}
          photo={editingPhoto}
          onSave={handleUpdate}
        />
      ) : (
        <div>
          <div style={{ display: "flex" }}>
            {photos.map((photo) => (
              <Photo
                key={photo._id}
                photo={photo}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            ))}
          </div>
          <div>
            <button onClick={() => setPage(page - 1)}>Previous</button>
            <button onClick={() => setPage(page + 1)}>Next</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
